import Link from "next/link";
import styled from "styled-components";

const ButtonWrap = styled.a`
  font-size: 16px;
  line-height: 16px;
  padding: 15px 24px;
  background-color: #0ac959;
  border: 0;
  outline: 0;
  color: #080559;
  margin-top: 12px;
  cursor: pointer;
  border: solid 3px #0ac959;
  transition-duration: 0.3s;
  :hover {
    border-color: #0800c9;
    color: #0800c9;
  }
`;

const ButtonLink = ({ children, className, href }) => {
  return (
    <Link href={href}>
      <>
        <ButtonWrap className={className}>{children}</ButtonWrap>;
      </>
    </Link>
  );
};

export default ButtonLink;
