import Image from "next/image";
import ClientThumbWrap from "../ClientThumbWrap";
import FlexWrapper from "../FlexWrapper";
import styled from "styled-components";

const ViewContainer = styled.div`
  height: 100%;
  width: 100%;
`;
const ImgWrapper = styled.div`
  position: relative;
  width: 100%;
  height: 400px;
  cursor: pointer;
  img {
    object-fit: cover;
  }
`;

const GridWrap = styled(FlexWrapper)`
  margin-left: -12px;
  margin-right: -12px;
`;

const Button = styled.button`
  font-size: 16px;
  line-height: 16px;
  padding: 15px 24px;
  background-color: #0ac959;
  border: 0;
  outline: 0;
  color: #080559;
  margin-top: 12px;
  cursor: pointer;
  border: solid 3px #0ac959;
  transition-duration: 0.3s;
  :hover {
    border-color: #0800c9;
    color: #0800c9;
  }
`;

const WorkDisplayer = ({ ...displayerProps }) => {
  if (viewer) {
    return (
      <>
        <FlexWrapper justify="space-between">
          <h2 style={{ fontSize: `36px`, marginBottom: `24px` }}>
            {viewer.clientName}
          </h2>
          <Close
            className="hover"
            style={{ width: `36px`, height: `36px`, cursor: `pointer` }}
            onClick={() => setViewer(false)}
          />
        </FlexWrapper>
        <ViewContainer width={clients.length} className="hover">
          <ImgWrapper
            onClick={() => {
              setContact(true);
              setContactTextSource("portfolio");
            }}
          >
            <Image
              src={viewer.imageThumb}
              alt="Picture of the author"
              layout="fill"
            />
          </ImgWrapper>
          <p style={{ marginTop: `36px` }}>
            This is som text about some stuff that goes on fever like this and
            it goes on on onon on.
          </p>
          <Button
            onClick={() => {
              setContact(true);
              setContactTextSource("portfolio");
            }}
          >
            More about this project
          </Button>
        </ViewContainer>
      </>
    );
  }
  return (
    <GridWrap wrap={true} justify="flex-start">
      {clients.map((client) => (
        <ClientThumbWrap
          setViewer={setViewer}
          client={client}
          blur={blur}
          key={client.id}
          discipline={discipline}
        />
      ))}
    </GridWrap>
  );
};

export default WorkDisplayer;
