import { useContext } from "react";
import Link from "next/link";
import FlexWrapper from "../FlexWrapper";
import styled from "styled-components";
import { useRouter } from "next/router";
import { BlobContext } from "../Context/BlobContext";

const LinksWrapper = styled(FlexWrapper)`
  background-color: white;
  height: 100%;
  display: flex;
  align-items: center;
  line-height: 30px;
  @media (max-width: 769px) {
    display: none;
  }
  a,
  button {
    cursor: pointer;
    font-size: 22px;
    color: #0800c9;
    min-width: 100px;
    width: 25%;
    text-align: center;
    :hover {
      text-decoration: underline;
    }
    :focus {
      outline: #0800c9 solid 1px;
    }
  }
  .green {
    background-color: #0ac959;
    color: #080559;
  }
  .purple {
    background-color: #5859cc;
    color: #ffffff;
  }
  .red {
    background-color: #c92014;
    color: #ffffff;
  }
  .yellow {
    background-color: #c9ab14;
    color: #080559;
  }
`;
const ContactButton = styled.button`
  background-color: transparent;
  border: none;
  cursor: pointer;
  font-size: 22px;
  color: #0800c9;
  line-height: 30px;
  padding: 0 12px;
  :hover {
    text-decoration: underline;
  }
  :focus {
    outline: #0800c9 solid 1px;
  }
`;

const HeaderMenu = () => {
  const router = useRouter();

  const { setContact, setContactTextSource } = useContext(BlobContext);

  return (
    <LinksWrapper justify="space-around" childrenMarginRight="12px">
      <Link href="/bio">
        <a
          className="c purple"
          style={
            router.pathname.includes("bio")
              ? { color: `#CC4537`, textDecoration: `underline` }
              : null
          }
        >
          Bio
        </a>
      </Link>
      <Link href="/work">
        <a
          className="c green"
          style={
            router.pathname.includes("work")
              ? { color: `#CC4537`, textDecoration: `underline` }
              : null
          }
        >
          Work
        </a>
      </Link>
      <Link href="/tech">
        <a
          className="c red"
          style={
            router.pathname.includes("tech")
              ? { color: `#CC4537`, textDecoration: `underline` }
              : null
          }
        >
          Tech
        </a>
      </Link>
      <ContactButton
        className="yellow"
        onClick={() => {
          setContact(true);
          setContactTextSource("contact");
        }}
      >
        Contact
      </ContactButton>
    </LinksWrapper>
  );
};

export default HeaderMenu;
