import * as React from 'react';
export const IkonateContext = React.createContext({});
const styleMap = {
    square: { strokeLinecap: 'square', strokeLinejoin: 'miter' },
    roundSquare: { strokeLinecap: 'round', strokeLinejoin: 'miter' },
    round: { strokeLinecap: 'round', strokeLinejoin: 'round' },
};
export const transformContext = ({ style = 'square', size, border = 2, color, }) => {
    const iconStyle = styleMap[style];
    return Object.assign({}, iconStyle, { strokeWidth: border, color, fontSize: size });
};
